import React, { useState } from 'react'

function Search() {
   const [name,setname]=useState("");
   const [songs, setSongs] = useState([]);
   const fetchData=(value) =>{
    fetch("http://localhost:5000/bgms")
    .then((response) => response.json())
     .then((json) =>{
   const res=json.filter((song)=>{
      return song.songname && song.songname.toLoweCase().includes(value.toLoweCase())
   })
   .catch((err)=>{
    console.log(err);
   })
    console.log(res);
   });
   }
  const handlechange =(value)=>{
    setname(value)
   fetchData(value)
   }
  

  return (
    <div className='Search'>
      <h1>HELLO</h1>
      <input placeholder='Type to search' value={name} onChange={(e)=>handlechange(e.target.value)} />
      <div>
            {songs.map((song) => (
               <div key={song._id}>
                  <p>{song.songname}</p>
               </div>))}
               </div>
    </div>
  )
}


export default Search;
